import styled from "styled-components"
import React from 'react'


const Container = styled.div`
width: 100%;
height: 100vh;
background: url("https://i.ibb.co/NSFLxBt/Signup-Login-Backdrop1.png");
background-size: cover;
display: flex;
align-items: center;
justify-content: center;    
`
const Wrapper = styled.div`
width: 25%;
padding: 20px;
background-color: white;
`
const Title = styled.h1`
display: flex;
align-items: center;
justify-content: center;  
font-size: 24px;
font-weight: 300;
`

const Form = styled.form`
display: flex;
flex-direction: column;
`

const Input = styled.input`
flex: 1;
min-width: 40%;
margin: 10px 0px;  
padding: 10px;
`


const Button = styled.button`
flex: auto;
width: 70%;
border: none;
padding: 15px 20px;
background-color: #1A6837;
color: white;
cursor: pointer;
margin-bottom: 10px;
`
const Link = styled.a`
margin: 5px 0px; 
font-size: 12px;
text-decoration: underline;
cursor: pointer;
`

const Login = () => {
  return (
    <Container>
        <Wrapper>
        <Title>Login</Title>
        <Form>
            <Input placeholder="Username"/>
            <Input placeholder="Password"/>
            <Button>Login</Button>
            <Link>Forgot Password?</Link>
        </Form>
        </Wrapper>
    </Container>
  )
}

export default Login
