import axios from "axios";

const BASE_URL = "http://localhost:5000/api/";
 const TOKEN ="eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzNWZjMWZlNGIzNmNkNzExNDU3YjI1ZiIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY2NzQxMTQ1NSwiZXhwIjoxNjY3NjcwNjU1fQ.8slljufQofUR0KpqeicZ5ocpFKkfnHuIuVPDDQPq7Tk";
 
export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  header: { token: `Bearer ${TOKEN}` },
});
